components/page/home-page-with-sidenav:
{{#if carousel}}
  <div class="slider{{#if theme_settings.slider_width '!==' 'full-width'}} in-container{{/if}} {{#if theme_settings.slider_width '===' 'full-width'}}full-width{{/if}}{{#if theme_settings.slide_content_background '===' 'without-bg'}} slide-content-no-bg{{/if}}">
    {{> components/carousel}}
    <div class="clear"></div>
  </div>
{{/if}}

<div class="main full home">
    <div class="right-main">

        {{> components/custom/sidenav}}

        {{#if banners.top}}
            <div class="banners top" data-banner-location="top">
                <div class="banner">
                {{{limit banners.top 1}}}
                </div> <!-- banner -->
                <div class="clear"></div>
            </div> <!-- banners -->
            <!-- <div class="clear"></div> -->
         {{/if}}

      <div class="featured-products-section">
        {{#if products.featured}}
            {{> components/products/featured products=products.featured columns=theme_settings.homepage_featured_products_column_count}}
        {{/if}}

        {{#if products.top_sellers}}
            {{> components/products/top products=products.top_sellers columns=theme_settings.homepage_top_products_column_count}}
        {{/if}}

        {{#if products.new}}
            {{> components/products/new products=products.new columns=theme_settings.homepage_new_products_column_count}}
        {{/if}}
        <div class="clear"></div>
      </div>

        {{#if banners.bottom}}
            <div class="banners bottom" data-banner-location="bottom">
                <div class="banner">
                {{{limit banners.bottom 1}}}
                </div> <!-- banner -->
            </div> <!-- banners -->
         {{/if}}
    </div> <!-- right-main -->

    <div class="clear"></div>

</div> <!-- main full home -->

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

